import logo from './logo.svg';
import './App.css';
import Todo from "./Todo.js"
import React, { useState } from 'react';

function App() {
  const [todos, setTodos] = useState([])
  const[textValue , setTextValue] = useState()

  function handleSubmit(i){
    setTodos([...todos , {"title":textValue,"key":Math.floor(Math.random()*10000),"finished":false}])
  }

  return (
    <div className="App">
      <h1>
        To-do list
      </h1>
      <form>
        <div>
          <input type="text" onChange={(e)=>(setTextValue(e))}/>
            <button type='submit'>
              Ekle
            </button>
        </div>
      </form>
    </div>
  );
}

export default App;
